import React, { useState, useEffect } from 'react';
import axios from 'axios';

const EventForm = ({ match, history }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [date, setDate] = useState('');
  const [location, setLocation] = useState('');

  useEffect(() => {
    if (match.params.id) {
      const fetchEvent = async () => {
        try {
          const response = await axios.get(`${process.env.REACT_APP_API_URL}/events/${match.params.id}`, {
            headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
          });
          const { title, description, date, location } = response.data;
          setTitle(title);
          setDescription(description);
          setDate(date);
          setLocation(location);
        } catch (error) {
          console.error('Failed to fetch event', error);
        }
      };

      fetchEvent();
    }
  }, [match.params.id]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      if (match.params.id) {
        await axios.put(`${process.env.REACT_APP_API_URL}/events/${match.params.id}`, { title, description, date, location }, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
      } else {
        await axios.post(`${process.env.REACT_APP_API_URL}/events`, { title, description, date, location }, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
      }
      history.push('/events');
    } catch (error) {
      console.error('Failed to save event', error);
    }
  }

  return (
    <div>
      <h2>{match.params.id ? 'Edit Event' : 'Create Event'}</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Title</label>
          <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required />
        </div>
        <div>
          <label>Description</label>
          <textarea value={description} onChange={(e) => setDescription(e.target.value)} />
        </div>
        <div>
          <label>Date</label>
          <input type="date" value={date} onChange={(e) => setDate(e.target.value)} required />
        </div>
        <div>
          <label>Location</label>
          <input type="text" value={location} onChange={(e) => setLocation(e.target.value)} required />
        </div>
        <button type="submit">{match.params.id ? 'Update' : 'Create'}</button>
      </form>
    </div>
  );
}

export default EventForm;
