import React, { useState, useEffect } from 'react';
import axios from 'axios';

const EventForm = () => {
  const [events, setEvents] = useState([]);
  const [newEvent, setNewEvent] = useState({
    title: '',
    description: '',
    date: '',
    location: '',
    imageUrl: '',
  });
  const [editIndex, setEditIndex] = useState(-1);

  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    try {
      const response = await axios.get('http://localhost:5000/events');
      setEvents(response.data);
    } catch (error) {
      console.error('Falha ao buscar eventos', error);
    }
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setNewEvent({ ...newEvent, [name]: value });
  };

  const addEvent = async () => {
    try {
      const token = localStorage.getItem('token'); // Obtenha o token de autenticação do armazenamento local
      const response = await axios.post('http://localhost:5000/events', newEvent, {
        headers: {
          Authorization: `Bearer ${token}`, // Adicione o token de autorização no cabeçalho da solicitação
        },
      });
      setEvents([...events, response.data]);
      setNewEvent({
        title: '',
        description: '',
        date: '',
        location: '',
        imageUrl: '',
      });
      alert('Evento criado com sucesso!');
    } catch (error) {
      console.error('Falha ao criar evento', error);
      alert('Falha ao criar evento. Por favor, tente novamente.');
    }
  };

  // Outras funções (editEvent, saveEvent, deleteEvent) permanecem as mesmas

  return (
    <div>
      <h2>Create Event</h2>
      <form onSubmit={editIndex === -1 ? (e) => { e.preventDefault(); addEvent(); } : saveEvent}>
        <div>
          <label>Title</label>
          <input
            type="text"
            name="title"
            value={newEvent.title}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Description</label>
          <textarea
            name="description"
            value={newEvent.description}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Date</label>
          <input
            type="date"
            name="date"
            value={newEvent.date}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Location</label>
          <input
            type="text"
            name="location"
            value={newEvent.location}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Image URL</label>
          <input
            type="text"
            name="imageUrl"
            value={newEvent.imageUrl}
            onChange={handleInputChange}
          />
        </div>
        <button type="submit">
          {editIndex === -1 ? 'Create Event' : 'Save Event'}
        </button>
      </form>
      <ul>
        {events.map((event, index) => (
          <li key={event._id}>
            <div>
              <strong>{event.title}</strong>
              <p>{event.description}</p>
              <p>Date: {new Date(event.date).toLocaleDateString()}</p>
              <p>Location: {event.location}</p>
              {event.imageUrl && <img src={event.imageUrl} alt="Event" />}
            </div>
            <div>
              <button onClick={() => editEvent(index)}>Edit</button>
              <button onClick={() => deleteEvent(index)}>Delete</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default EventForm;
